<template>
    <view class="network-tip" v-if="show">
      <view class="wrapper">
        <view class="close-wrap"><image class="close" :src="closeImgBase64Src"  @click="closeTip"></image></view>
        <image class="img-1" :src="tipImgBase64Src"></image>
        <view class="_p-tip">网络已断开，请检查网络后重试</view>
      </view>
    </view>
</template>

<script>
  export default {
    data() {
      return {
        show: false,
        closeImgBase64Src: 'data:image/png;base64,',
        tipImgBase64Src: 'data:image/png;base64,',
        
      }
    },
    watch: {
      show(val) {
        if (val) {
          document.body.style.overflow = 'hidden';
          document.documentElement.style.overflow = 'hidden';
        } else {
          document.body.style.overflow = '';
          document.documentElement.style.overflow = '';
        }
      }
    },
    methods: {
      showTip(params) {
        this.show = true
      },
      closeTip() {
        this.show = false
      }
    }
  };
</script>

